import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import '../css/MovieList.css'

class MovieList extends Component {
  constructor (props){
    super(props);
    this.state = {
      movies: []
    };
    this.setRatingColour = this.setRatingColour.bind(this);
    this.formatDate = this.formatDate.bind(this);
  }

  componentWillReceiveProps(props) {
    this.setState({movies: props.movies})
  }

  setRatingColour(averageRating){
    var ratingColour = ""
    if (averageRating >= 75) {
      ratingColour = "RatingGood";
    } else if (averageRating >= 50 && averageRating < 75){
      ratingColour = "RatingModerate";
    } else {
      ratingColour = "RatingBad"
    }
    return ratingColour;
  }

  formatDate(releasedOn){
    if (releasedOn !== undefined){
      var releasedOn = releasedOn.split("-")
      var releaseDate = new Date(releasedOn[0], releasedOn[1])
      var month = releaseDate.toLocaleString('default', { month: 'long' });
      var date = releasedOn[0] + " " + month
    } else {
      date = "TBA"
    }
    return date
  }

  render() {
    return(
      <>
        <div className="col-md-8 mt-0 CollectionHeading">
          <h2 className="">Popular Movies</h2>
        </div>
        <div className="row">
          {this.state.movies.map(item => (
            <div className="col-6 col-sm-4 col-md-3 col-lg-2 col-xl-2 mb-4">
              <div className="MovieItem" key={ item.id }>
                <div className={`Rating ${this.setRatingColour(item.average)} `}>
                  <span>{ item.average }%</span>
                </div>
                <Link to={ item.link }><img className="MainImage" src={ item.image } alt=""></img></Link>
                <p className="MovieTitle">{ item.title } </p>
                <p className="MovieDate mt-2 mb-4">{ this.formatDate(item.date) }</p>
              </div>
            </div>
          ))}
        </div>
      </>
    )
  }
}

export default MovieList;
