import React, { Component } from 'react';
import { Link } from 'react-router-dom'
import '../css/MovieList.css'

class MovieList extends Component {
  constructor (props){
    super(props);
    this.state = {
      movies: []
    };
    this.setRatingColour = this.setRatingColour.bind(this);
  }

  componentWillReceiveProps(props) {
    this.setState({movies: props.movies})
  }

  setRatingColour(averageRating){
    if(averageRating >= 50 && averageRating < 75){
      return "RatingModerate"
    } else if (averageRating >= 75) {
      return "RatingGood"
    } else {
      return "RatingBad"
    }
    return "RatingBad"
  }

  render() {
    return(
      <div className="row">
        {this.state.movies.map(item => (
          <div className="col-6 col-sm-4 col-md-3 col-lg-2 col-xl-2 mb-4">
            <div className="MovieItem" key={item.id}>
              <div className={this.setRatingColour(item.average)}>
                <span>{item.average}%</span>
              </div>
              <Link to={item.link}><img className="MainImage" src={item.image} alt=""></img></Link>
              <p className="MovieTitle">{item.title} </p>
              <p className="MovieDate mt-2 mb-4">{item.date}</p>
            </div>
          </div>
        ))}
      </div>
    )
  }
}

export default MovieList;
