import React, { Component } from 'react';

class Show extends Component {
  constructor (props){
  super(props);
  this.state = {
    movie: ''
  };
}

componentDidMount() {

  let id = this.props.match.params.id
  fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=6ed12e064b90ae1290fa326ce9e790ff&language=en-US`)
    .then(res => {
      return res.json();
    })
    .then( (data) => {
        var url = "https://image.tmdb.org/t/p/w600_and_h900_bestv2/";
        var poster = url + data.poster_path;
        var backdrop = "https://image.tmdb.org/t/p/w780/" + data.backdrop_path;

        function movieLength(runtime)
          {
            const hours = Math.floor(runtime / 60);
            const minutes = runtime % 60;
            return `${hours}h ${minutes}min`;
          }

        var release_date = "Unknown";

        if ( data.release_date !== undefined){
          release_date = data.release_date.split("-")[0]
        };

        var element = (
          <div className="ShowMovie">
            <div className="Backdrop">
              <Back history={this.props.history} />
              <img className="BackdropImage" src={backdrop} alt=""></img>
            </div>
            <div className="Content">
              <div className="ItemDetails">
                <div className="ItemImage">
                  <img className="ItemPoster" src={poster} alt=""></img>
                </div>
                 <div className="ItemInfo">
                   <h1>{data.original_title}</h1>
                   <p>{release_date} â€¢ {data.vote_average * 10} User Score</p>
                   <p>{movieLength(data.runtime)}</p>
                 </div>
               </div>
               <div>
                <div className="MovieDescription">
                  <h2>Overview</h2>
                  <p className="ItemDescription">
                    {data.overview}
                  </p>
                </div>
               </div>
             </div>
          </div>
        )
      this.setState({ movie: element })
      })
    }

  render() {
    return(
      <div>
        {this.state.movie}
      </div>
    )
  }
}


export default Show;
