import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'
import Header from "./Header"
import MovieList from "./MovieList"

class Movies extends Component {
  constructor (props){
  super(props);
  this.state = {
    filtered: [],
      movies: [],
      title: "Popular Movies"
  };
    this.formatResults = this.formatResults.bind(this);
    this.search = this.search.bind(this);
    this.handleKeyDown = this.handleKeyDown.bind(this);
}

  setStyle(){

  }

  formatResults(results) {
    var els = results.map((mov) => {
      var imageLink = "https://image.tmdb.org/t/p/w600_and_h900_bestv2/" + mov.poster_path;
      var imageStyle = {
        background: "url(" + imageLink + ")",

      };

      let colour = ""
      let average = mov.vote_average * 10;
        if(average >= 50 && average < 75){
          colour = "#350095";
        } else if (average >= 75) {
          colour = "#2FCC5F";
        } else {
          colour = "#BF004A"
        }

      var ratingColor = {
        background: colour
      }

      var months = ["January", "February", "March", "April", "May", "June", "July", "August", "October", "November", "December"]

      if (mov.release_date !== undefined){
        var release_date = mov.release_date.split("-")
        var month = parseInt(release_date[1]) - 1
        var date = months[month] + " " + release_date[0]
      }
      let id = `/movie/${mov.id}`

      return (
          {
            title: mov.original_title,
            date: date,
            image: imageLink,
            average: average,
            imageStyle: imageStyle,
            link: id,
            ratingColor: ratingColor
          }
        )
    });
    this.setState({ filtered: els })
  }

  componentDidMount() {
    fetch("https://api.themoviedb.org/3/trending/all/day?api_key=6ed12e064b90ae1290fa326ce9e790ff&language=en-US")
    .then(res => {
      return res.json();
    })
    .then( (data) => {
      this.setState({
        movies: data.results
      });
      this.formatResults(data.results)
    })
  }

  componentWillReceiveProps(nextProps) {
    this.setState({
      filtered: nextProps.items
    });
  }

  handleError() {
    this.props.history.push(`error/`);
  }

  handleKeyDown(e) {
    let heading = document.querySelector(".CollectionHeading").firstElementChild;
    let query = e.target.value;
       if (e.key === 'Enter') {
         this.search()
       }
       if (query === "") {
         this.formatResults(this.state.movies)
         heading.innerText = "Popular Movies"
       }
     }

  search(e) {
    let heading = document.querySelector(".CollectionHeading").firstElementChild;
    let query = document.querySelector(".SearchInput").value;

    if (query !== "") {
      heading.innerText = query + " Results:";
      // Keep a reference to the current movies in state
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=6ed12e064b90ae1290fa326ce9e790ff&language=en-US&&page=1&include_adult=false&query=${query}`)
      .then(res => {
        return res.json();
      })
      .then( (data) => {
        let filtered = data.results.filter(function(x){
          return x.adult !== undefined
        })
        this.formatResults(filtered)
      });
    }
  }
  render() {
    return(
      <div>
        <Header />
        <div className="container">
          <div className="row">
            <div className="Search col-md-8">
              <span>
                <FontAwesomeIcon
                  icon={faSearch}
                  onClick={this.search}/>
              </span>
              <input type="text"
                className="SearchInput"
                placeholder="Search"
                onKeyUp={this.handleKeyDown} />
              </div>
            </div>
            <div className="col-md-8 mt-0 CollectionHeading">
              <h2 className="">Popular Movies</h2>
            </div>
          <MovieList movies={this.state.filtered} 
                     title={this.state.title} />
        </div>
      </div>
    )
  }
}

export default Movies;
