import React, { Component } from 'react';
import '../css/Movies.css'
import { Link } from 'react-router-dom'
class MovieList extends Component {
  constructor (props){
  super(props);
  this.state = {
    movies: []
  };
}

  componentWillReceiveProps(nextProps) {
    this.setState({movies: nextProps.movies})
  }

  render() {
    return(
      <div className="row">
      {this.state.movies.map(item => (
      <div className="col-6 col-sm-4 col-md-3 col-lg-2 col-xl-2 mb-4">
        <div className="MovieItem" key={item.id}>
            <div className="Rating" style={item.ratingColor}>
              <span>{item.average}%</span>
            </div>
             <Link to={item.link}><img className="MainImage" onClick={this.handleNext} src={item.image} alt=""></img></Link>
            <p className="MovieTitle">{item.title} </p>
            <p className="MovieDate">{item.date}</p>
          </div>
          </div>
        ))}
      </div>
    )
  }
}

export default MovieList;
